import React from 'react';
import './App.css';
import { Button } from 'reactstrap';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      visible: false,
      inputData: '',
    };
  }
  //Input change logic
  handleInputChange = (event) => {
    this.setState({
      inputData: event.target.value

    })

  }

  //Form submit logic
  handleSubmit = (event) => {
    alert('A name was submitted: ' + inputData);
    event.preventDefault();
    this.componentDidMount();
  }


  componentDidMount() {
    console.log(inputData)
    var {inputData} = this.state
    var apiURL = "https://vat.erply.com/numbers?vatNumber=";
    var idLookUp = inputData;
    fetch(apiURL + idLookUp)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  generateItem() {


    var {isLoaded, items, visible} = this.state;
    //console.log(inputData)
    if (this.state.visible) { 

      return (<ul>
        {Object.keys(items).map(key => 
          <li>
            {key}: {items[key]}           
          </li>)}
        </ul>)
    } else {
      return <h4>Click the button to Fetch Data</h4>
    }

  }

  render() {
    var {isLoaded, items} = this.state;
    const {inputData} = this.state;
    if(!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App-header">
          <div>
            <p>Requested data: {inputData}</p>
            <form onSubmit={this.handleSubmit}>
                <input type="text" placeholder="Select item by ID" value={this.state.inputData} onChange={this.handleInputChange}>
                </input>
                <button onClick={() => this.setState({visible: true})}>Click Me</button>
                {this.generateItem()}


            </form>
          </div>
            {/* kuvab listina fetchitud datat
             <ul>
            {Object.keys(items).map(key => 
              <li>
                {key}: {items[key]}           
              </li>)}
            </ul> */}
        </div>
      );
    }

  }
}

export default App;
