import React from 'react';
import './App.css';
import { Button } from 'reactstrap';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    fetch('https://vat.erply.com/numbers?vatNumber=EE100247019')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  // Object.keys(items).map(key => <div>{key}: {items[key]}</div>)


  render() {
    var {isLoaded, items} = this.state;
    if(!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App-header">
          <div>
          <form>Input</form>
          <Button color="primary">Fetch data</Button>{' '}
          </div>
            {/* <ul>
              {items.map(item => (
                <li key={item.id}>
                  {item.name}
                </li>

              ))};
            </ul> */}
            <ul>
            {Object.keys(items).map(key => 
            <li>
               {key}: {items[key]}           
            </li>)}

            </ul>

          {/* <header className="App-header">

            <p>
              Edit <code>src/App.js</code> and save to reload.
            </p>
            <a
              className="App-link"
              href="https://reactjs.org"
              target="_blank"
              rel="noopener noreferrer"
            >
              Learn React
            </a>
          </header> */}
        </div>
      );
    }

  }
}


export default App;
