import React from 'react';

import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  componentDidMount() {

    fetch('http://jsonplaceholder.typicode.com/users')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  render() {

    var {isLoaded, items} = this.state;

    if(!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div classname ="App">
          Data has been loaded
        </div>
      );
    }

  }
}

export default App;
