import React from 'react';
import './App.css';
import { Button } from 'reactstrap';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      visible: false,
    }
  }


  componentDidMount() {
    var apiURL = "https://vat.erply.com/numbers?vatNumber=EE100247019"
    fetch(apiURL)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  generateItem() {
    var {isLoaded, items} = this.state;
    if (this.state.visible) {
      return (<ul>
        {Object.keys(items).map(key => 
          <li>
            {key}: {items[key]}           
          </li>)}
        </ul>)
    }
    return <h4>Click the button to Fetch Data</h4>
  }



  render() {
    var {isLoaded, items} = this.state;
    if(!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App-header">
          <div>
          <Button color="primary" onClick={() => this.setState({visible: true})}>Click Me</Button>{' '}
          {this.generateItem()}
          </div>
            {/* kuvab listina datat
             <ul>
            {Object.keys(items).map(key => 
              <li>
                {key}: {items[key]}           
              </li>)}
            </ul> */}
        </div>
      );
    }

  }
}

export default App;
