import React from 'react';
import './App.css';
import { Button } from 'reactstrap';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      visible: false,
      // Kasutame value, et saada inputist tekst kätte
      inputData: null,
    };
  }
  //Input logic
  handleInputChange(event) {
    //this.setState({value: event.target.value});
    event.preventDefault()
    console.log(event.target.name)
    console.log(event.target.value)
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  //Input logic
  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }


  componentDidMount() {
    var apiURL = "https://vat.erply.com/numbers?vatNumber=";
    var idLookUp = "EE100247019";
    fetch(apiURL + idLookUp)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  generateItem() {
    var {isLoaded, items} = this.state;
    if (this.state.visible) {
      return (<ul>
        {Object.keys(items).map(key => 
          <li>
            {key}: {items[key]}           
          </li>)}
        </ul>)
    }
    return <h4>Click the button to Fetch Data</h4>
  }



  render() {
    var {isLoaded, items} = this.state;
    const {inputData} = this.state;
    if(!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App-header">
          <div>
            <p>Requested data: {inputData}</p>
            <form onSubmit={this.handleSubmit}>
              <label>ID: </label>
                <input type="text" placeholder="Select item by ID" name="inputData" onChange={this.handleInputChange}>
                </input>
                <Button color="primary" onClick={() => this.setState({visible: true})}>Click Me</Button>{' '}
                {this.generateItem()}

            </form>
          </div>
            {/* kuvab listina datat
             <ul>
            {Object.keys(items).map(key => 
              <li>
                {key}: {items[key]}           
              </li>)}
            </ul> */}
        </div>
      );
    }

  }
}

export default App;
