import React from 'react';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      visible: false,
      inputData: '',
    };
  }
  //Input change logic
  handleInputChange = (event) => {
    this.setState({
      inputData: event.target.value

    })

  }

  //Form submit logic
  handleSubmit = (event) => {
    event.preventDefault();
    this.componentDidMount();
  }


  componentDidMount() {
    var {inputData} = this.state
    var apiURL = "https://vat.erply.com/numbers?vatNumber=";
    fetch(apiURL + inputData)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  generateItem() {
    var {items} = this.state;
    if (this.state.visible) { 
      return (<ul>
        {Object.keys(items).map(key => 
          <li>
            {key}: {items[key]}           
          </li>)}
        </ul>)
    } else {
      return <h4>Click the button to Fetch Data</h4>
    }

  }

  render() {
    var {isLoaded, inputData} = this.state;
    if(!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App-header">
          <div>
            <p>Requested data: {inputData}</p>
            <form onSubmit={this.handleSubmit}>
                <input type="text" placeholder="Select item by ID" pattern=".{5,10}" required title="Password must be between 5 and 10 characters" onChange={this.handleInputChange}>
                </input>
                <button onClick={() => this.setState({visible: true})}>Click Me</button>
            </form>
            {this.generateItem()}
          </div>
        </div>
      );
    }

  }
}

export default App;
