import React from 'react';
import './App.css';
import { Button } from 'reactstrap';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  fetchData = async () => {
    const response = await axios.get(
      'https://vat.erply.com/numbers?vatNumber=EE100247019'
    );

    setBooks(response.data);
  };

  componentDidMount() {
    fetch('https://vat.erply.com/numbers?vatNumber=EE100247019')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  // Object.keys(items).map(key => <div>{key}: {items[key]}</div>)


  render() {
    var {isLoaded, items} = this.state;
    if(!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App-header">
          <div>
          <Button color="primary" onClick={console.log(items)}>Fetch data</Button>{' '}
          </div>
            {/* <ul>
              {items.map(item => (
                <li key={item.id}>
                  {item.name}
                </li>

              ))};
            </ul> */}
            <ul>
            {Object.keys(items).map(key => 
              <li>
                {key}: {items[key]}           
              </li>)}
            </ul>
        </div>
      );
    }

  }
}

export default App;
