import React from 'react';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    fetch('https://vat.erply.com/numbers?vatNumber=EE100247019')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  render() {
    var {isLoaded, items} = this.state;
    if(!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="App-header">
          <h1>Header</h1>
            <ul>
              {items.map(item => (
                <li key={item.id}>
                  {item.Name}
                </li>
              ))};
            </ul>



          {/* <header className="App-header">

