import React from 'react';
import './App.css';
import { Button } from 'reactstrap';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      visible: false,
      // Kasutame value, et saada inputist tekst kätte
      value: '',
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  //Input logic
  handleChange(event) {
    this.setState({value: event.target.value});
  }
  //Input logic
  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }


  componentDidMount() {
    var apiURL = "https://vat.erply.com/numbers?vatNumber=";
    var idLookUp = "EE100247019";
    fetch(apiURL + idLookUp)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  generateItem() {
    var {isLoaded, items} = this.state;
    if (this.state.visible) {
      return (<ul>
        {Object.keys(items).map(key => 
          <li>
            {key}: {items[key]}           
          </li>)}
        </ul>)
    }
    return <h4>Click the button to Fetch Data</h4>
  }



  render() {
    var {isLoaded, items} = this.state;
    if(!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App-header">
          <div>
            <form>
              <label>
                ID:
                <input type="text" value={this.state.value} onChange={this.handleChange} >

                </input>
                  <Button color="primary" onClick={() => this.setState({visible: true},{value: this.state.value})}>Click Me</Button>{' '}
                  {this.generateItem()}
              </label>
            </form>
          </div>
            {/* kuvab listina datat
             <ul>
            {Object.keys(items).map(key => 
              <li>
                {key}: {items[key]}           
              </li>)}
            </ul> */}
        </div>
      );
    }

  }
}

export default App;
